@js:
if(!baseUrl.match(/_2/)){
page="{{@@class.chapterPages@tag.a.-1@text}}"
if(page!=""){
page=String(page).match(/(\d+)/)[1];
list=[];
for(i=2;i<=page;i++){
	if(i==2 || i==5){
	url=baseUrl.replace(/(_\d+)*\.html$/,'_'+i+'.html,{"webView": true}')
	}else if(i==3){
		url=baseUrl.replace(/(_\d+)*\.html$/,'_'+i+'.html,{"method":"POST","body":"err=1"}')
		}else{
		url=baseUrl.replace(/(_\d+)*\.html$/,'_'+i+'.html')
	}
	list.push(url)
	}
list
}
}

